public class code1 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a = 10;
		System.out.println("Byte data is:"+a);
		short s = a;//implicit type conversion
		System.out.println("Short data is:"+s);
		int i = a;//implicit type conversion
		System.out.println("int data is:"+i);
		long l = a;//implicit type conversion
		System.out.println("long data is:"+l);
		float f = a;//implicit type conversion
		System.out.println("float data is:"+f);
		double d = a;//implicit type conversion
		System.out.println("double data is:"+d);

	}

}
