//love and affection
public class code2 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double d = 10;
		System.out.println("Double data is:"+d);
		float f = (float)d;//explicit type conversion
		System.out.println("float data is:"+f);
		long l = (long)d;//explicit type conversion
		System.out.println("long data is:"+l);
		int i = (int)d;//explicit type conversion
		System.out.println("int data is:"+i);
		short s = (short)d;//explicit type conversion
		System.out.println("Short data is:"+s);
		byte b = (byte)d;//explicit type conversion
		System.out.println("byte data is:"+b);
		

	}

}
