public class code5 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 121;
		System.out.println("Int data is : "+i);
		char c1 = (char)i;//explicit type conversion
		System.out.println("char data is : "+c1);
		
		byte b = 121;
		System.out.println("Byte data is : "+b);
		char c2 = (char)b;//explicit type conversion
		System.out.println("char data is : "+c2);
		
		short s = 121;
		System.out.println("short data is : "+s);
		char c3 = (char)s;//explicit type conversion
		System.out.println("char data is : "+c3);
		
		long l = 121;
		System.out.println("long data is : "+l);
		char c4 = (char)l;//explicit type conversion
		System.out.println("char data is : "+c4);
		
		float f = 121;
		System.out.println("float data is : "+f);
		char c5 = (char)f;//explicit type conversion
		System.out.println("char data is : "+c5);
		
		float f1 = 121.567f;
		System.out.println("float1 data is : "+f);
		char c6 = (char)f1;//explicit type conversion
		System.out.println("char data is : "+c6);
		
		double d1 = 121;
		System.out.println("double1 data is : "+d1);
		char c7 = (char)d1;//explicit type conversion
		System.out.println("char data is : "+c7);
		
		double d2 = 121.567;
		System.out.println("double2 data is : "+d2);
		char c8 = (char)d2;//explicit type conversion
		System.out.println("char data is : "+c8);
		}

}
