public class code7 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//for loop
		for(int i=1; i<=5; i++) {
			System.out.println("Sachin tendulkar");
		}
		
		//while loop
		int j = 1;
		while(j<=5) {
			System.out.println("Rahul Dravid");
			j++;
			}
		
		//do while loop
		//success condition
		int k = 1;
		do {
			System.out.println("Mahendra Singh Dhoni");
			k++;
		} while(k<=5);
		
		//failure condition
				int m = 1;
				do {
					System.out.println("Mahendra Singh Dhoni");
					k++;
				} while(m<=-5);
			
		}

	}


